import React, { useState, useEffect } from "react";
import { TextField, TablePagination, Container, Typography, Button, Box } from "@mui/material";
import ContactForm from "./components/ContactForm";
import ContactsTable from "./components/ContactsTable";
import axios from "axios";

const App = () => {
  const [contacts, setContacts] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);
  const [formOpen, setFormOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [page, setPage] = useState(1);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [totalContacts, setTotalContacts] = useState(0);

  const fetchContacts = async () => {
    try {
      const response = await axios.get("http://localhost:3001/contacts", {
        params: {
          page,
          limit: rowsPerPage,
          search: searchQuery,
        },
      });
      setContacts(response.data.data);
      setTotalContacts(response.data.total);
    } catch (error) {
      console.error("Error fetching contacts:", error);
    }
  };

  // Update page or rows per page
  const handlePageChange = (event, newPage) => {
    setPage(newPage + 1); // Material-UI uses 0-based indexing for pages
    fetchContacts();
  };

  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(1); // Reset to the first page
    fetchContacts();
  };

  // Update search query and refetch contacts
  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
    fetchContacts();
  };


  useEffect(() => {
    fetchContacts();
  }, []);

  const handleAddContact = async (contact) => {
    try {
      console.log("Contact data being sent:", contact); // Debugging log
      const response = await axios.post("http://localhost:3001/contacts", contact);
      setContacts([...contacts, response.data]);
    } catch (error) {
      console.error("Error adding contact:", error);
    }
  };


  const handleEditContact = async (updatedContact) => {
    try {
      const response = await axios.put(
        `http://localhost:3001/contacts/${updatedContact.id}`,
        updatedContact
      );
      setContacts(
        contacts.map((contact) =>
          contact.id === updatedContact.id ? response.data : contact
        )
      );
      setSelectedContact(null);
      setFormOpen(false);
    } catch (error) {
      console.error("Error editing contact:", error);
    }
  };

  const handleDeleteContact = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/contacts/${id}`);
      setContacts(contacts.filter((contact) => contact.id !== id));
    } catch (error) {
      console.error("Error deleting contact:", error);
    }
  };
  return (
    <Container>
      <Typography variant="h2" align="center" sx={{ fontWeight: 'bold', color: 'white', background: 'black' }} gutterBottom >
        Contact Management
      </Typography>
      <Box mb={2}>
        <Button variant="contained" onClick={() => setFormOpen(true)}>
          Add Contact
        </Button>
      </Box>
      <Box mb={2}>
        <TextField
          label="Search Contacts"
          variant="outlined"
          value={searchQuery}
          onChange={handleSearch}
          fullWidth
        />
      </Box>

      <ContactsTable
        contacts={contacts}
        onEdit={(contact) => {
          setSelectedContact(contact);
          setFormOpen(true);
        }}
        onDelete={handleDeleteContact}
      />
      <TablePagination
        component="div"
        count={totalContacts} // Total number of contacts
        page={page - 1} // Material-UI uses 0-based indexing
        onPageChange={handlePageChange}
        rowsPerPage={rowsPerPage}
        onRowsPerPageChange={handleRowsPerPageChange}
      />

      {formOpen && (
        <ContactForm
          contact={selectedContact}
          onSave={(contact) =>
            selectedContact ? handleEditContact(contact) : handleAddContact(contact)
          }
          onClose={() => {
            setFormOpen(false);
            setSelectedContact(null);
          }}
        />
      )}
    </Container>
  );
};

export default App;
