import React from "react";
import {
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Button,
    Paper,
} from "@mui/material";

const ContactsTable = ({ contacts, onEdit, onDelete }) => {
    return (
        <TableContainer
            component={Paper}
            sx={{
                boxShadow: 2,
                borderRadius: 2,
                maxWidth: 1000,
                margin: "auto",
                mt: 4,
                backgroundColor: "#F8FDF8", // Very light background for the table container
            }}
        >
            <Table>
                <TableHead>
                    <TableRow
                        sx={{
                            backgroundColor: "#7BE495", // Soft green for header
                        }}
                    >
                        <TableCell sx={{ fontWeight: "bold", color: "#205072" }}>
                            First Name
                        </TableCell>
                        <TableCell sx={{ fontWeight: "bold", color: "#205072" }}>
                            Last Name
                        </TableCell>
                        <TableCell sx={{ fontWeight: "bold", color: "#205072" }}>
                            Email
                        </TableCell>
                        <TableCell sx={{ fontWeight: "bold", color: "#205072" }}>
                            Phone
                        </TableCell>
                        <TableCell sx={{ fontWeight: "bold", color: "#205072" }}>
                            Company
                        </TableCell>
                        <TableCell sx={{ fontWeight: "bold", color: "#205072" }}>
                            Job Title
                        </TableCell>
                        <TableCell sx={{ fontWeight: "bold", color: "#205072" }}>
                            Actions
                        </TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {contacts.map((contact, index) => (
                        <TableRow
                            key={contact.id}
                            sx={{
                                backgroundColor: index % 2 === 0 ? "#CFF4D2" : "#F8FDF8", // Alternating light backgrounds
                                "&:hover": {
                                    backgroundColor: "#56C596", // Subtle highlight on hover
                                    color: "#ffffff", // White text on hover
                                },
                            }}
                        >
                            <TableCell>{contact.first_name}</TableCell>
                            <TableCell>{contact.last_name}</TableCell>
                            <TableCell>{contact.email}</TableCell>
                            <TableCell>{contact.phone}</TableCell>
                            <TableCell>{contact.company}</TableCell>
                            <TableCell>{contact.job_title}</TableCell>
                            <TableCell>
                                <Button
                                    onClick={() => onEdit(contact)}
                                    variant="outlined"
                                    size="small"
                                    sx={{
                                        borderColor: "#205072",
                                        color: "#205072",
                                        "&:hover": {
                                            borderColor: "#329D9C",
                                            backgroundColor: "#CFF4D2",
                                        },
                                    }}
                                >
                                    Edit
                                </Button>
                                <Button
                                    onClick={() => onDelete(contact.id)}
                                    variant="contained"
                                    size="small"
                                    sx={{
                                        backgroundColor: "#205072",
                                        color: "#ffffff",
                                        ml: 1,
                                        "&:hover": {
                                            backgroundColor: "#329D9C",
                                        },
                                    }}
                                >
                                    Delete
                                </Button>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
};

export default ContactsTable;
