import React, { useState, useEffect } from "react";
import { Box, TextField, Button, Card, CardContent, Typography, InputAdornment } from "@mui/material";
import PersonIcon from "@mui/icons-material/Person";
import EmailIcon from "@mui/icons-material/Email";
import PhoneIcon from "@mui/icons-material/Phone";
import BusinessIcon from "@mui/icons-material/Business";
import WorkIcon from "@mui/icons-material/Work";

const ContactForm = ({ contact, onSave, onClose }) => {
    const [formData, setFormData] = useState({
        first_name: "",
        last_name: "",
        email: "",
        phone: "",
        company: "",
        job_title: "",
    });

    useEffect(() => {
        if (contact) {
            setFormData(contact);
        }
    }, [contact]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(formData);
    };

    return (
        <Card
            sx={{
                maxWidth: 600,
                mx: "auto",
                mt: 4,
                boxShadow: 4,
                borderRadius: 5,
                backgroundColor: "#ffffff",
            }}
        >
            <CardContent>
                <Typography
                    variant="h5"
                    align="center"
                    gutterBottom
                    sx={{
                        fontWeight: 'bold',
                        color: '#2c6975'
                    }}
                >
                    {contact ? 'Edit Contact' : 'Add Contact'}
                    <Box
                        sx={{
                            width: '50px',
                            height: '2px',
                            backgroundColor: '#68b5a0',
                            margin: 'auto',
                            mt: 1
                        }}
                    />
                </Typography>
                <Box
                    component="form"
                    onSubmit={handleSubmit}
                    sx={{
                        "& .MuiTextField-root": { mb: 2 },
                        "& .MuiButton-root": { textTransform: "none" },
                    }}
                >
                    {["first_name", "last_name", "phone", "email", "company", "job_title"].map((field) => (
                        <TextField
                            key={field}
                            label={field.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                            name={field}
                            value={formData[field]}
                            onChange={handleChange}
                            fullWidth
                            required={["first_name", "last_name", "phone", "email"].includes(field)}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        {field === "first_name" || field === "last_name" ? <PersonIcon /> :
                                            field === "phone" ? <PhoneIcon /> :
                                                field === "email" ? <EmailIcon /> :
                                                    field === "company" ? <BusinessIcon /> :
                                                        <WorkIcon />}
                                    </InputAdornment>
                                ),
                            }}
                            sx={{
                                '& .MuiOutlinedInput-root': {
                                    '& fieldset': {
                                        borderColor: '#2c6975',
                                    },
                                    '&:hover fieldset': {
                                        borderColor: '#68b5a0',
                                    },
                                }
                            }}
                        />
                    ))}
                    <Box mt={3} display="flex" justifyContent="space-between">
                        <Button
                            type="submit"
                            variant="contained"
                            sx={{
                                backgroundColor: '#68b5a0',
                                '&:hover': { backgroundColor: '#2c6975' },
                            }}
                        >
                            Done
                        </Button>
                        <Button
                            variant="outlined"
                            onClick={onClose}
                            sx={{
                                borderColor: '#2c6975',
                                color: '#2c6975',
                                '&:hover': {
                                    backgroundColor: '#cde0c9',
                                },
                            }}
                        >
                            Cancel
                        </Button>
                    </Box>
                </Box>
            </CardContent>
        </Card>
    );
};

export default ContactForm;
