import React, { useState, useEffect } from "react";
import { Box, TextField, Button } from "@mui/material";

const ContactForm = ({ contact, onSave, onClose }) => {
    const [formData, setFormData] = useState({
        first_name: "",
        last_name: "",
        email: "",
        phone: "",
        company: "",
        job_title: "",
    });

    useEffect(() => {
        if (contact) {
            setFormData(contact);
        }
    }, [contact]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Form data submitted:", formData); // Debugging log
        onSave(formData); // Pass form data to handleAddContact
    };


    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ maxWidth: 400, mx: "auto" }}>
            <TextField
                label="First Name"
                name="first_name" // Matches backend expectation
                value={formData.first_name}
                onChange={handleChange}
                required
            />
            <TextField
                label="Last Name"
                name="last_name" // Matches backend expectation
                value={formData.last_name}
                onChange={handleChange}
                required
            />
            <TextField
                label="Email"
                name="email" // Matches backend expectation
                value={formData.email}
                onChange={handleChange}
                required
            />

            <TextField
                label="Phone"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                fullWidth
                margin="normal"
            />
            <TextField
                label="Company"
                name="company"
                value={formData.company}
                onChange={handleChange}
                fullWidth
                margin="normal"
            />
            <TextField
                label="Job Title"
                name="job_title"
                value={formData.job_title}
                onChange={handleChange}
                fullWidth
                margin="normal"
            />
            <Box mt={2} display="flex" justifyContent="space-between">
                <Button type="submit" variant="contained">
                    Save
                </Button>
                <Button variant="outlined" onClick={onClose}>
                    Cancel
                </Button>
            </Box>
        </Box>
    );
};

export default ContactForm;
