import React, { useState } from "react";
import { Dialog, DialogTitle, DialogContent, TextField, Button, Box } from "@mui/material";

const ContactForm = ({ contact = {}, onSave, onClose }) => {
    const [formData, setFormData] = useState({
        name: contact.name || "",
        email: contact.email || "",
        phone: contact.phone || "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(formData);
    };

    return (
        <Dialog open onClose={onClose} maxWidth="sm" fullWidth>
            <DialogTitle
                sx={{
                    backgroundColor: "#2c6975",
                    color: "#ffffff",
                }}
            >
                {contact.id ? "Edit Contact" : "Add Contact"}
            </DialogTitle>
            <DialogContent>
                <Box
                    component="form"
                    onSubmit={handleSubmit}
                    sx={{
                        "& .MuiTextField-root": {
                            marginBottom: 2,
                            "& .MuiOutlinedInput-root": {
                                backgroundColor: "#e0ecde",
                                "&:hover fieldset": { borderColor: "#68bsa0" },
                            },
                            "& .MuiInputLabel-root": { color: "#2c6975" },
                        },
                    }}
                >
                    <TextField
                        label="Name"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        fullWidth
                        required
                    />
                    <TextField
                        label="Email"
                        name="email"
                        type="email"
                        value={formData.email}
                        onChange={handleChange}
                        fullWidth
                        required
                    />
                    <TextField
                        label="Phone"
                        name="phone"
                        type="tel"
                        value={formData.phone}
                        onChange={handleChange}
                        fullWidth
                        required
                    />
                    <Box
                        sx={{
                            display: "flex",
                            justifyContent: "space-between",
                            marginTop: 2,
                        }}
                    >
                        <Button
                            variant="contained"
                            onClick={onClose}
                            sx={{
                                backgroundColor: "#cde0c9",
                                color: "#2c6975",
                                "&:hover": { backgroundColor: "#e0ecde" },
                            }}
                        >
                            Cancel
                        </Button>
                        <Button
                            variant="contained"
                            type="submit"
                            sx={{
                                backgroundColor: "#68bsa0",
                                "&:hover": { backgroundColor: "#2c6975" },
                            }}
                        >
                            Save
                        </Button>
                    </Box>
                </Box>
            </DialogContent>
        </Dialog>
    );
};

export default ContactForm;
